def LE_showInAllCam(nodePath):
    for camName, mask in LE_CAM_MASKS.items():
        nodePath.show(mask)

def LE_showInOneCam(nodePath, thisCamName):
    LE_showInAllCam(nodePath)
    for camName, mask in LE_CAM_MASKS.items():
        if camName != thisCamName:
            nodePath.hide(mask)
