class ActionDeleteObjById(ActionBase):
    """ Action class for deleting object """

    def __init__(self, editor, uid):
        self.editor = editor
        function = self.editor.objectMgr.removeObjectById
        self.uid = uid
        ActionBase.__init__(self, function, self.uid)
        self.hierarchy = {}
        self.objInfos = {}
        self.objTransforms = {}
