def activate(self):
        self.scalingNode.reparentTo(self)
        self.fActive = 1

    def deactivate(self):
        self.scalingNode.reparentTo(hidden)
        self.fActive = 0

    def showWidgetIfActive(self):
        if self.fActive:
            self.reparentTo(ShowBaseGlobal.direct.group)

    def showWidget(self):
        self.reparentTo(ShowBaseGlobal.direct.group)

    def hideWidget(self):
        self.reparentTo(hidden)
