            for event in subEvents:
                self.addEvent(event)

    def destroy(self):
        if hasattr(self, '_name'):
            # keep this around
            #del self._doneEvent
            del self._name
            del self._subEvents
            del self._completedEvents
            self.ignoreAll()

    def getName(self):
        return self._name

    def getDoneEvent(self):
        return self._doneEvent

    def isCompleted(self):
        return self._completed
