            for i in range(int(self.frameNum/5)):
                dc.DrawLine(self.points[i][0][0], self.points[i][0][1], self.points[i][1][0], self.points[i][1][1])

            for i in range(self.frameNum/5):
                st = str(self.numbers[i])
                tw,th = dc.GetTextExtent(st)
